    private void numStateChanged(javax.swing.event.ChangeEvent evt) {//GEN-FIRST:event_numStateChanged
        // TODO add your handling code here:
        int valor = Integer.parseInt(num.getValue().toString());
        int res = 1;
        int cont = valor;
        String lista = "";
        while (cont > 1){
            lista += cont + " x ";
            res *= cont;
            cont--;
        }
        String fatorial = Integer.toString(res);
        lblF.setText(lista + "1 = " + fatorial);
    }//GEN-LAST:event_numStateChanged

